// Function to normalize the environment variables (convert to lowercase)
function normalizeEnvVar(value) {
  if (value === true || value === false) {
    return value;
  }
  return value ? value.toLowerCase() : "";
}

// Retrieve and normalize the environment variables
var lightColor = normalizeEnvVar(env.get("LIGHT_COLOR"));
var lightMode = normalizeEnvVar(env.get("LIGHT_MODE"));
var lightType = normalizeEnvVar(env.get("PRODUCT_MODEL"));

var value = "";

// Define the mapping
if (lightType === "type_a") {
  if (lightMode === "off") {
    value = 0x60;
  } else if (lightColor === "red") {
    if (lightMode === "blink") {
      value = 0x21;
    } else if (lightMode === "flashing") {
      value = 0x31;
    } else if (lightMode === "on") {
      value = 0x11;
    } else {
      node.warn("Please check your configuration");
    }
  } else if (lightColor === "green") {
    if (lightMode === "blink") {
      value = 0x23;
    } else if (lightMode === "flashing") {
      value = 0x33;
    } else if (lightMode === "on") {
      value = 0x13;
    } else {
      node.warn("Please check your configuration");
    }
  } else if (lightColor === "yellow") {
    if (lightMode === "blink") {
      value = 0x22;
    } else if (lightMode === "flashing") {
      value = 0x32;
    } else if (lightMode === "on") {
      value = 0x12;
    } else {
      node.warn("Please check your configuration");
    }
  } else {
    node.warn("Please check your configuration");
  }
  // Convert the hexadecimal value to decimal
  var decimalValue = value;
  msg.payload = {
    value: decimalValue,
    fc: 6,
    unitid: 1,
    address: 194,
    quantity: 1, // Number of coils/registers to write (for single coil, it's 1)
  };
} else if (lightType === "type_b") {
  if (lightMode === "off") {
    value = 4;
  } else if (lightColor === "red") {
    if (lightMode === "on") {
      value = 0; // Always_On red light
    } else if (lightMode === "blink") {
      value = 8; // Blink red light
    } else {
      node.warn("Please check your configuration");
    }
  } else if (lightColor === "green") {
    if (lightMode === "on") {
      value = 1; // Always_On green light
    } else if (lightMode === "blink") {
      value = 9; // Blink green light
    } else {
      node.warn("Please check your configuration");
    }
  } else if (lightColor === "blue") {
    if (lightMode === "on") {
      value = 2; // Always_On green light
    } else if (lightMode === "blink") {
      value = 10; // Blink green light
    } else {
      node.warn("Please check your configuration");
    }
  } else {
    node.warn("Please check your configuration");
  }

  // Set the payload for Modbus communication
  msg.payload = {
    fc: 5, // Function code for "Write Single Coil"
    unitid: 1, // Modbus Unit ID
    address: value, // Starting address (adjust based on your setup)
    quantity: 1, // Number of coils/registers to write (for single coil, it's 1)
  };
} else {
  node.warn("Please check your configuration");
}

// // Set the output message
// if (notification) {
//     msg.payload = notification;
// } else {
// }
return msg;
